mapper.select:
select count(user_acco)
from t_sys_user
where user_acco = #{driverAccount}

    AND user_code != #{driverCode}

select count(user_acco)
from t_sys_user
where is_delete = 0
and user_acco = #{driverAccount}

    AND user_code != #{driverCode}

select count(phone)
from t_sys_user
where is_delete = 0
and phone = #{phone}

    AND user_code != #{driverCode}
