mapper.select:
select count(*)
from t_sys_store
where is_delete = 0
    and bus_lis_code = #{busLisCode}

    and store_code != #{storeCode}

select store.store_code as storeCode, store.store_name as storeName, store.user_code as userCode, `user`.user_name as userName, `user`.user_acco as userAccount, store.phone as phone, store.inv_code as invCode, store.province_code as provinceCode, store.city_code as cityCode, store.area_code as areaCode, store.province as province, store.city as city, store.area as area, store.detailed_addr as detailedAddress, store.version from t_sys_store store left join t_sys_user `user` on (store.user_code = `user`.user_code) where store.is_delete = 0
AND store.user_code = #{operator}
AND store.store_code like concat ('%',#{storeCode},'%')
AND store.store_name like concat ('%',#{storeName},'%')
AND `user`.user_name like concat ('%',#{userName},'%')
AND store.province_code = #{provinceCode}
AND store.city_code = #{cityCode}
AND store.area_code = #{areaCode}
order by store.create_time desc
