mapper.select:
select count(user_acco)
from t_sys_user
where user_acco = #{userAccount}

    and user_code != #{userCode}

select count(user_acco)
from t_sys_user
where is_delete = 0
    and user_acco = #{userAccount}

    and user_code != #{userCode}

select count(phone)
from t_sys_user
where is_delete = 0
    and phone = #{phone}

    and user_code != #{userCode}
